parameters.jsonBody:
={
  "model": "gemma3:latest",
  "messages": [
    {
      "role": "system",
      "content": "You are an AI that can analyze images. Your output is not the final answer instead you provide a detailed description of the image. Your output should specifically extract as much relevant information for the users query to allow another AI agent to provide an answer."
    },
    {
      "role": "user",
      "content": [
        {
          "type": "text",
          "text": "{{ $json.chatInput }}"
        },
        {
          "type": "image_url",
          "image_url": {
            "url": "data:image/jpeg;base64,{{ $json.data }}"
          }
        }
      ]
    }
  ]
}
